#!/bin/bash packages=( "jq" ) c=0
if [[ $(brew --version | sed 1q) != "command not found: brew" ]] then brewv="$(brew --version | sed 1q) ✅" for i in "${packages[@]}" do if [[ "$(command -v ${i})" != "" ]] then package[c++]="$(brew list --versions ${i}) ✅" else package[c++]="${i} is not installed 🚧 | It needs Homebrew to be installed ⚠️" fi done else brewv="Hombrew is not installed 🚧" fi
cat << EOB { "items": [ { "title": "Homebrew", "subtitle": "${brewv}", "arg": "brew", "autocomplete": "Homebrew", "icon": { "path": "homebrew-icon.png" } }, { "title": "Jq", "subtitle": "${package[0]}", "arg": "jq", "autocomplete": "Jq", "icon": { "path": "jq-icon.png" } } ] } EOB

#!/bin/bash
raw='
{"uid":"Online documention","title":"Online documention","subtitle":"Open online cwebp documentation","arg":"online","autocomplete":"Online documention","icon":{"path":"globe-icon.png"}}
'
doc_json=$(jq '.' ./docs.txt)
temp=$(echo "$raw$doc_json")
final=$(jq -s '{items : .}' <<< $temp)

cat << EOB
${final}
EOB

#!/bin/bash
preset="{query}"

jq -R 'split(",") | {title: .[0], subtitle: .[1], arg: (.[0] + "," + .[1]), autocomplete: .[0], icon:{path:"preset-icon.png"}}' <<< "${preset}"

