#!/bin/bash RAW=( $_links_list ) IMAGES=() PARAMS="{query}"
for LINK in "${RAW[@]}"; do if [[ -d "${LINK}" ]] then IMAGES+=("$(find -E "${LINK}" /dev -maxdepth 1 -iregex '.*\.(png|jpg|jpeg|tif|tiff|webp)')") else IMAGES+=("${LINK}") fi done
for IMAGE in "${IMAGES[@]}"; do 2>&1 cwebp eval " ${PARAMS}" "${IMAGE}" -o "${IMAGE%.*}.webp" done
