#!/bin/bash
packages=( "jq" "cpanm" )
c=0

if [[ $(brew --version | sed 1q) != "command not found: brew" ]]
then
 brewv="$(brew --version | sed 1q) ✅"
  for i in "${packages[@]}"
  do
    if [[ "$(command -v ${i})" != "" ]]
    then 
      package[c++]="$(brew list --versions ${i}) ✅"
    else
      package[c++]="${i} is not installed 🚧 | Hit Enter to install ⬇️ | Need Homebrew to be installed ⚠️"
    fi
  done
else
  brewv="Hombrew is not installed 🚧 | Hit Enter to install ⬇️"
fi

if [[ $(2>&1 perldoc -l Mojolicious) != "No documentation found for \"Mojolicious\"." ]]
then
    package_mojo="Mojolicious $(perl -MMojolicious -e 'print $Mojolicious::VERSION ."\n"') ✅"
else
    package_mojo="Mojolicious is not installed 🚧 | Hit Enter to install ⬇️ | Need Cpanm to be installed ⚠️"
fi

cat << EOB
{
"items": [
   {
    "title": "Homebrew",
    "subtitle": "${brewv}",
    "arg": "brew",
    "autocomplete": "Homebrew",
    "icon": {
        "path": "homebrew-icon.png"
      }
    },
    {
    "title": "Jq",
    "subtitle": "${package[0]}",
    "arg": "jq",
    "autocomplete": "Jq",
    "icon": {
        "path": "jq-icon.png"
      }
    },
    {
    "title": "Cpanm",
    "subtitle": "${package[1]}",
    "arg": "cpanm",
    "autocomplete": "Cpanm",
    "icon": {
        "path": "perl-icon.png"
      }
    },
    {
    "title": "Mojolicious",
    "subtitle": "${package_mojo}",
    "arg": "mojo",
    "autocomplete": "Mojolicious",
    "icon": {
        "path": "mojo-icon.png"
      }
    }
  ]
}
EOB

#!/usr/bin/env perl

use strict;
use warnings;
use Mojo::UserAgent;

# Get HTML page of cwebp documentation
my $url = Mojo::URL->new('https://developers.google.com/speed/webp/docs/cwebp');
my $ua = Mojo::UserAgent->new;
my $dom = $ua->get($url)->result->dom;

# Title of options are under dt > code tags
my $titles = $dom->find('dt > code')->map('text')->join("|");

# Put a delimiter for next script
print $titles, '&&&&&';

# Description of options are under dd tags
for my $descr ($dom->find('dd')->each) {
    print $descr->all_text, '|';
}

#!/bin/bash
raw='
{"uid":"Online documention","title":"Online documention","subtitle":"Open online cwebp documentation","arg":"online","autocomplete":"Online documention","icon":{"path":"globe-icon.png"}}
{"uid":"Refresh documentation","title":"Refresh documentation","subtitle":"Refresh Alfred cwebp documentation","arg":"refresh","autocomplete":"Refresh documentation","icon":{"path":"refresh-icon.png"}}
'
doc_json=$(jq '.' ${doc_folder}/docs.txt)
temp=$(echo "$raw$doc_json")
final=$(jq -s '{items : .}' <<< $temp)

cat << EOB
